import React, { useState } from "react";
import "./DebtPop.css"; // Import external CSS file

const DebtPop = () => {
  const [debtAmount, setDebtAmount] = useState("");
  const [bank, setBank] = useState("");
  const [totalPrice, setTotalPrice] = useState(null);
  const [expenses, setExpenses] = useState(null);

  //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

  const [AiResponse, setAiResponse] = useState("");
  const [Loading, setLoading] = useState(false);

  const handleSubmit = async (e) => {};

  const bankPercentages = {
    kaspi: 0.01,
    kaspi_debt: 0.02,
    kaspi_deposit: 0.03,
    jusan_debt: 0.04,
    jusan_deposit: 0.05,
  };

  // Handle debt amount change
  const handleDebtAmountChange = (e) => {
    setDebtAmount(e.target.value);
  };

  // Handle bank selection change
  const handleBankChange = (e) => {
    setBank(e.target.value);
  };

  // Handle the "Done" button click
  const handleDoneClick = async () => {
    if (debtAmount && bankPercentages[bank] !== undefined) {
      e.preventDefault();
      setLoading(true);

      const res = await fetch("https://openrouter.ai/api/v1/chat/completions", {
        method: "POST",
        headers: {
          Authorization:
            "Bearer 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",
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          model: "deepseek/deepseek-r1:free",
          messages: [
            {
              role: "user",
              content: "hello",
            },
          ],
        }),
      });

      setLoading(false);

      const data = await res.json();
      console.log(data.choices[0].message.content);
      setAiResponse(data.choices[0].message.content);

      const debt = parseFloat(debtAmount);
      const percentage = bankPercentages[bank]; // Get percentage based on selected bank
      const calculatedExpenses = debt * percentage; // Calculate expenses based on percentage
      const calculatedTotalPrice = debt + calculatedExpenses;

      setExpenses(calculatedExpenses);
      setTotalPrice(calculatedTotalPrice);
    }
  };

  return (
    <div className="layout-container">
      <div className="layout-content-container">
        {/* Banner Section */}
        <div
          className="banner"
          style={{
            backgroundImage:
              "url('https://cdn.usegalileo.ai/sdxl10/a4e96856-6ef3-4d66-93da-a341083ec375.png')",
          }}
        ></div>

        {/* Title */}
        <h3>DebtPop</h3>

        {/* Debt Input */}
        <div className="input-container">
          <label>
            <p>Enter the debt amount</p>
            <input
              type="number"
              placeholder="100,000"
              value={debtAmount}
              onChange={handleDebtAmountChange}
            />
          </label>
        </div>

        {/* Bank Selection */}
        <div className="input-container">
          <label>
            <p>Select bank</p>
            <select value={bank} onChange={handleBankChange}>
              <option value="kaspi">Kaspi Red</option>
              <option value="kaspi_debt">Kaspi Кредит</option>
              <option value="kaspi_deposit">Kaspi Депозит</option>
              <option value="jusan_debt">Jusan Кредит</option>
              <option value="jusan_deposit">Jusan Депозит</option>
            </select>
          </label>
        </div>

        {/* Price Summary */}
        {totalPrice !== null && expenses !== null && (
          <div className="price-summary">
            <div>
              <p>Total final price</p>
              <p>{totalPrice}</p>
            </div>
            <div>
              <p>Expenses</p>
              <p>{expenses}</p>
            </div>
          </div>
        )}

        {/* Hints Section */}
        <h3>Hints and Recommendations</h3>
        {[
          "Pay off your debt as soon as possible",
          "Try to negotiate with the bank for a better rate",
          "If you have multiple debts, consider consolidating them",
        ].map((hint, index) => (
          <div key={index} className="hint-container">
            <div className="hint-icon">
              <svg
                xmlns="http://www.w3.org/2000/svg"
                width="24px"
                height="24px"
                fill="currentColor"
                viewBox="0 0 256 256"
              >
                <path d="M176,232a8,8,0,0,1-8,8H88a8,8,0,0,1,0-16h80A8,8,0,0,1,176,232Zm40-128a87.55,87.55,0,0,1-33.64,69.21A16.24,16.24,0,0,0,176,186v6a16,16,0,0,1-16,16H96a16,16,0,0,1-16-16v-6a16,16,0,0,0-6.23-12.66A87.59,87.59,0,0,1,40,104.49C39.74,56.83,78.26,17.14,125.88,16A88,88,0,0,1,216,104Z" />
              </svg>
            </div>
            <p className="hint-text">{hint}</p>
          </div>
        ))}

        <div className="button-container">
          <button
            onClick={handleDoneClick}
            disabled={!debtAmount || isNaN(debtAmount) || !bank}
          >
            Done
          </button>
        </div>

        <div className="">
          <button onClick={handleSubmit}>Done</button>
        </div>

        <textarea value={Loading ? "typing..." : AiResponse}></textarea>
      </div>
    </div>
  );
};

export default DebtPop;
